package com.syntax.class02;

public class area {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        
		
		int width=5;
		int height=8;
		int area=width*height;
		int perimeter=2*width+2*height;
		System.out.println("The perimeter of a rectangle with 5 width  and 8 height is equal to "+ perimeter +" and the area is "+ area+".") ;
	}

}
